INSERT INTO `vaccines` VALUES (13,'БЦЖ','В 1 - 4 дни жизни ребенка (еще в роддоме) – делают прививки от туберкулеза (БЦЖ) и гепатита В. Ревакцинация в 6 лет.',1,4,'day','/vaccines/2019/12/28/5e0706423d7af.png',NULL,'2019-12-12 11:15:09','2020-01-15 11:21:24'),(15,'АбКДС','В 3 месяца делают повторные прививки от коклюша, дифтерии и столбняка (АКДС), полиомиелита и гемофильной инфекции типа b.',3,NULL,'month','/vaccines/2020/01/15/5e1ee265c9355.jpeg',NULL,'2019-12-12 11:48:10','2020-01-15 12:59:01'),(16,'АбКДС','В 4 месяца – 3 этап вакцинирования от гепатита, полиомиелита, гемофильной инфекции типа b и коклюша, дифтерии и столбняка, а также 2-ая прививка от пневмококковой инфекции.',4,NULL,'month','/vaccines/2020/01/15/5e1ee27755150.jpeg',NULL,'2019-12-12 11:59:37','2020-01-15 12:59:19'),(18,'ОПВ','В возрасте 12-15 месяцев нужно сделать прививку от кори, краснухи и паротита (свинки), повторить вакцинирование от полиомиелита и пневмококковой инфекции.',12,15,'month','/vaccines/2020/01/15/5e1ee289256f9.jpeg',NULL,'2019-12-12 12:26:35','2020-01-15 12:59:37'),(19,'АбКДС','В 1,5 года делается ревакцинация АКДС, полиомиелита и гемофильной инфекции типа b. Еще несколько прививок ждут детей в школьном возрасте.',18,NULL,'month',NULL,NULL,'2019-12-12 12:30:16','2020-01-15 12:26:14'),(20,'БЦЖ','В 6 лет (в 1 классе) еще раз делается БЦЖ, прививка от кори, краснухи и паротита и АКДС.',6,NULL,'year',NULL,NULL,'2019-12-12 13:05:10','2020-01-15 12:29:58'),(21,'АДС-М','В 16 лет делается прививка от столбняка и дифтерии (АДС-м), которую в дальнейшем нужно будет повторять каждые 10 лет.',16,NULL,'year','/vaccines/2020/01/15/5e1edfe45e83a.jpeg',NULL,'2019-12-12 13:09:54','2020-01-15 12:48:20'),(29,'ВГВ','В 1 - 4 дни жизни ребенка (еще в роддоме) – делают прививки от туберкулеза (БЦЖ) и гепатита В. Ревакцинация в 6 лет.',1,4,'day',NULL,NULL,'2020-01-15 11:24:30','2020-01-15 11:24:30'),(30,'АбКДС','В 2 месяца малыши получают комплексную прививку против гепатита, полиомиелита, коклюша, диф